def translationDesFacette(valeurDeTranslation, lstF):
        x = valeurDeTranslation
        for elt in range(0, len(lstF)) :
            for elt2 in range(0, 3):
                lstF[elt][elt2][-1] = lstF[elt][elt2][-1] + x
        return lstF

